import React, { useState } from 'react';
import './App.css';

// Book component
const Book = ({ activeTab }) => {
  const content = {
    tab1: "Content for tab 1",
    tab2: "Content for tab 2",
    tab3: "Content for tab 3"
  };

  return (
    <div className="book">
      <div className="page">
        {content[activeTab]}
      </div>
    </div>
  );
};

// Tabs component
const Tabs = ({ setActiveTab }) => {
  const tabs = ["tab1", "tab2", "tab3"];

  return (
    <div className="tabs">
      {tabs.map(tab => (
        <div key={tab} className="tab" onClick={() => setActiveTab(tab)}>
          {tab}
        </div>
      ))}
    </div>
  );
};

// App component
function App() {
  const [activeTab, setActiveTab] = useState("tab1");

  return (
    <div className="app">
      <Tabs setActiveTab={setActiveTab} />
      <Book activeTab={activeTab} />
    </div>
  );
}

export default App;
